#!/usr/bin/env node

var optsWithArgs = [ '--scripts-version' ];
var hasPath = false;

for (var t = 2; !hasPath && t < process.argv.length; ++t) {

    var arg = process.argv[t];

    if (optsWithArgs.indexOf(arg) !== -1) {
        t += 1;
    } else if (arg === '--') {
        hasPath = t + 1 < process.argv.length;
    } else if (arg[0] === '-') {
        // pass
    } else {
        hasPath = true;
    }

}

var args = process.argv.slice(2);

if (!hasPath)
    args.unshift('.');

var cp = require('child_process');
cp.spawnSync(__dirname + '/node_modules/.bin/create-react-app', args, { stdio: 'inherit' });
